    if (data === undefined) {
        console.log('data is undefined');   
        node.error("file undefined", msg);
    }

    var array = data.toString().split("\n");

    var measurement = [];
    for(var line in array) {
        var register = array[line];

        var sensor = {};
        sensor.date = moment(register.substring(2, 12), "YYMMDDHHmm").toDate();
        sensor.channel = register.substring(12, 14);
        sensor.reason = register.substring(14, 16);
        sensor.value = parseFloat(register.substring(16));

        // add sensor measure to the collection
        measurement.push(sensor);
    }

    // initialize context measurement
    msg.collection = 'Track';
    msg.operation  = 'insertMany';
    msg.payload = measurement;
    msg.gateway = gateway;

    console.log('Persist ' + msg.payload.length + ' measures');

    node.send(msg);
});

return;

