fs.readFile('/home/miguel/temp/nodered/accelerometer/' + msg.file, function(err, data) {
    if (err) 
        node.error("Reading file error", msg);

    var array = data.toString().split("\n");

    // define channels position and name
    var Time=0
    var X=1, CHANNEL_X="CHANNEL_X";
    var Y=2, CHANNEL_Y="CHANNEL_Y";
    var Z=3, CHANNEL_Z="CHANNEL_Z";

    // measurement collection
    var measurement = [];

    // hub name and measurement moment
    var hub;
    var startDate;
    for(var line in array) {
        var register = array[line];

        // get hub name
        if (register == '# [Recorder]') {
            hub = array[Number(line)+2].substring(7, array[Number(line)+2].length);
            console.log('Hub name: ' + hub);
        }

        // get start date/time
        if (register == '# [File]') {
            startDate = moment(array[Number(line)+3].substring(12, array[Number(line)+3].length) +
                               " " +
                               array[Number(line)+4].substring(12, array[Number(line)+4].length),
                              "YYYY-MM-DD HH:mm:ss.sss").toDate();
            console.log('Start date: ' + startDate);
        }

        // get measurements
        if (register.substring(0, 1) != '#') {
            var token = register.toString().split(" ");

            // get X channel sata
            var sensor_X = {};
            sensor_X.date = moment(startDate).add(token[Time], 'seconds').toDate();
            sensor_X.channel = CHANNEL_X;
            sensor_X.reason = null;
            sensor_X.value = parseFloat(token[X]);

            measurement.push(sensor_X);

            // get Y channel sata
            var sensor_Y = {};
            sensor_Y.date = moment(startDate).add(token[Time], 'seconds').toDate();
            sensor_Y.channel = CHANNEL_Y;
            sensor_Y.reason = null;
            sensor_Y.value = parseFloat(token[Y]);

            measurement.push(sensor_Y);

            // get Z channel sata
            var sensor_Z = {};
            sensor_Z.date = moment(startDate).add(token[Time], 'seconds').toDate();
            sensor_Z.channel = CHANNEL_Z;
            sensor_Z.reason = null;
            sensor_Z.value = parseFloat(token[Z]);

            measurement.push(sensor_Z);           
        }
    }

    // initialize context measurement
    msg.collection = 'Track';
    msg.operation  = 'insertMany';
    msg.payload    = [];
    msg.payload.push(measurement);    

    console.log('Persist ' + msg.payload[0].length + ' measures');

    node.send(msg);
});

return;

