var loopback = context.global.loopback;

var DeviceHub = loopback.models.DeviceHub;
var Device = loopback.models.Device;
var Track = loopback.models.Track;

DeviceHub.findOne({ where: {code: msg.gateway}}, function (err, deviceHub) {
    if (err) node.error("DeviceHub findOne error", msg);

    // generate track collection 
    msg.payload.forEach(function(measurement) {
        Device.findOne({ where: {deviceHubId: deviceHub.id, code: measurement.channel}}, function (err, device) {
            if (err) node.error("Device findOne error", msg);

            if(device !== null) {
                console.log('Channel: ' + measurement.channel + ' is Device id: ' + device.id + ' value:' + measurement.value + ' date: ' + measurement.date); 

                // persist track collection
                Track.create({'deviceId': device.id, 'value': measurement.value, 'date': measurement.date}, function (err, track) {
